import { Component, Input } from '@angular/core';
import { PricingPlan } from '../../shared/models/pricing-plans.models';

@Component({
  selector: 'app-pricing-plan',
  templateUrl: './pricing-plan.component.html',
  styleUrls: ['./pricing-plan.component.css']
})
export class PricingPlanComponent {
  @Input() public plan!: PricingPlan;
}
<div class="pricing-plan-header">
    <div class="pricing-plan-image">
        <mat-icon [svgIcon]="plan.imageSrc">
        </mat-icon>
    </div>
    <div class="pricing-plan-trial">{{plan.trialInfo}}</div>
</div>
<div class="pricing-plan-title">
    {{plan.title}}
</div>
<div class="pricing-plan-description">{{plan.description}}</div>
<div class="pricing-plan-options">
    <div class="pricing-plan-option"></div>
</div>
<button></button>
