import { Component } from '@angular/core';

@Component({
  selector: 'app-mainbanner',
  templateUrl: './mainbanner.component.html',
  styleUrls: ['./mainbanner.component.css']
})
export class MainbannerComponent {

}
<section class="wrapper">
    <div class="left">
        <h1>
            Everything your practice needs to <span>thrive</span>
        </h1>
        <p>
        The all-in-one healthcare marketing and medical practice management solution that enhances every patient touchpoint.
        </p>
        <button class="green-button" mat-button>Get started</button>
    </div>
    <img class="right" src="../assets/Public health-amico 1.png"/>
</section>
